import {Component, OnInit} from '@angular/core';
import {AccountService} from "../../servicies/account.service";
import {Router} from "@angular/router";
import {FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  errorLogin: string ='';
  constructor(private accountService: AccountService, private router: Router) { }

  ngOnInit() {
    // this.getUserWithToken(localStorage.getItem('my-token'));
  }

  formLogin = new FormGroup({
    email: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  })

  login(): void {
    const form = this.formLogin.value;
    console.log(form)
    // this.createAccount(form.email!, form.password!, form.password2!);
    this.getToken(form.email!, form.password!);
  }

  getToken(email: string, password: string): void {
    this.accountService.getToken(email, password).subscribe(
      data => {
        console.log(data);
        localStorage.setItem('my-token', data.auth_token);
        this.router.navigate(['/main']);
      },
      error => {
        console.log(error);
        this.errorLogin = 'Вы ввели неверные данные';
      }
    )
  }

  getUserWithToken(MyToken: any): void {
    this.accountService.getAccountWhithToken(MyToken).subscribe(
      data => {
        console.log(data);
      },
      error => {
        console.log(error);
      }
    )
  }
}
<header class="header">
  <div class="header__container">
    <a class="header__logo" routerLink="/main">
      <img class="header__logo_img" src="../../../assets/img/header/logo.png" alt="logo">
      <span class="header__logo_text">Master of Dungeons</span>
    </a>
    <div class="header__nav">
      <span class="header__text">Ещё нет учетной записи ?</span>
      <a routerLink="/registration" class="header__link">РЕГИСТРАЦИЯ</a>
    </div>
  </div>
</header>

<main class="login__container">
  <div class="login__window">
    <h2 class="login__form-h">Войти</h2>
    <span class="text-error">{{ errorLogin }}</span>
    <form class="form" [formGroup]="formLogin" (ngSubmit)="login()">
      <input placeholder="Электронная почта" type="email" formControlName="email"/>
      <input placeholder="Пароль" type="password" formControlName="password"/>
      <button tuiButton type="submit" appearance="outline"
              class="tui-space_right-3 tui-space_bottom-3 btn-submit" size="s">
        Подтвердить
      </button>
    </form>
    <div class="footer__container">
      <span class="footer__text">Ещё нет учетной записи ?</span>
      <a routerLink="/registration" class="footer__link">Регистрация</a>
    </div>
  </div>
</main>
