@Injectable({
  providedIn: 'root'
})
export class TaskService {
  constructor(private http: HttpClient) { }
  private apiUrl = 'http://localhost:8000/api/tasks/';

  getTasks(): Observable<Task[]> {
      return this.http.get<Task[]>(this.apiUrl);
