import {Component, OnInit} from '@angular/core';
import {AccountService} from "../../servicies/account.service";

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit{
  user: any;

  constructor(private accountService: AccountService) {  }
  ngOnInit() {
    this.getUserWithToken(localStorage.getItem('my-token'));
  }

  getUserWithToken(MyToken: any): void {
    this.accountService.getAccountWhithToken(MyToken).subscribe(
      data => {
        this.user = data;
        console.log(this.user);
      },
      error => {
        // console.log(error);
      }
    )
  }
}
<app-header></app-header>
<app-navigation-bar></app-navigation-bar>
<div class="main-block">
  <div class="profile__container">
    <div class="profile__nav-container">
      <h2 class="profile__title">Информация о профиле</h2>
    </div>
    <div class="profile__block-container">
      <div class="profile__block-group">Фамилия: {{ user.last_name }}</div>
      <div class="profile__block-group">Имя: {{ user.first_name }}</div>
      <div class="profile__block-group">Отчество: {{ user.patronymic }}</div>
    </div>
    <div class="profile__block-container">
      <div class="profile__block-group">email: {{ user.email }}</div>
    </div>
    <div class="profile__block-container">
      <div class="profile__block-group">Место учебы: {{ user.location }}</div>
      <div class="profile__block-group">Номер школы: {{ user.school_number }}</div>
      <div class="profile__block-group">{{ user.is_teacher }}</div>
      <div class="profile__block-group">{{ user.gender }}</div>
    </div>
  </div>
</div>
