import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {FormControl, FormGroup} from "@angular/forms";

@Component({
  selector: 'app-dialog-window',
  templateUrl: './dialog-window.component.html',
  styleUrls: ['./dialog-window.component.css']
})
export class DialogWindowComponent implements OnInit{

  @Output() protected isOpen: EventEmitter<boolean> = new EventEmitter<boolean>();
  dateControl = new FormControl();

  protected formPost!: FormGroup;
  protected formDoubleDate!: FormGroup;
  protected formSingleDate!: FormGroup;

  constructor() {}

  ngOnInit() {
    this.formPost = new FormGroup({
      newPost: new FormControl(),
      newSalary: new FormControl(),
    })

    this.formDoubleDate = new FormGroup({
      endDate: new FormControl(),
    })
  }

  protected manageDialog() {
    this.isOpen.emit(false);
  }

  submit(){

  }
}
<tui-root>
  <div class="wrapper"></div>
  <div class="dialog__container">
    <div class="dialog__container_outer">
      <div class="dialog__container_inner">
        <h2 class="dialog__title">Добавить запись</h2>

        <div>
          <form class="dialog__form" [formGroup]="formDoubleDate" (ngSubmit)="submit()">
            <label class="dialog__label">Дата окончания</label>
            <input type="date" name="date_interview" formControlName="endDate" tuiTextfield/>
          </form>
        </div>

        <div class="dialog__buttons">
          <button tuiButton type="submit" class="dialog__add" (click)="submit()">
            Добавить
          </button>
          <button tuiButton type="button" appearance="outline"
                  class="tui-space_right-3 tui-space_bottom-3 dialog__cancel"
                  (click)="manageDialog()">
            Отмена
          </button>
        </div>
      </div>
    </div>
  </div>
</tui-root>
