<div>
  <div *ngFor="let task of tasks">
    <span>{{ task.id }} {{ task.name }} {{ task.description }}</span>
  </div>
</div>

<form (ngSubmit)="createTask()">
    <input type="text" [(ngModel)]="newTask.name" name="title" placeholder="Title">
    <input type="text" [(ngModel)]="newTask.description" name="description" placeholder="Description">
    <button type="submit">Create Task</button>
</form>
import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import {TaskService} from "../../servicies/task.service";
// import { Task } from './task';

export interface Task {
    id: number;
    name: string;
    description: string;
    created_at: string;
}

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})
export class TaskListComponent implements OnInit{
  protected tasks?: Task[];
  newTask: Task = { id: 1, name: '', description: '', created_at: '' };

  constructor(private http: HttpClient) { }
  private apiUrl = 'http://localhost:8000/api/tasks/';

  getTasks(): Observable<Task[]> {
      return this.http.get<Task[]>(this.apiUrl);
  }

  // constructor(private taskService: TaskService) {
  // }

  ngOnInit() {
    this.getTasks().subscribe(
      tasks => {
        console.log(tasks)
        this.tasks = tasks;
    });
  }

  createTask() {
      this.http.post(this.apiUrl, this.newTask)
      .subscribe(() => {
          // code to refresh the task list goes here
      });
  }
}
