import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {GroupService} from "../../servicies/group.service";

@Component({
  selector: 'app-dialog-window',
  templateUrl: './dialog-window.component.html',
  styleUrls: ['./dialog-window.component.css']
})
export class DialogWindowComponent implements OnInit{
  error ='';
  @Output() protected isOpen: EventEmitter<boolean> = new EventEmitter<boolean>();
  protected formCreateGroup!: FormGroup;

  constructor(private groupService: GroupService) {  }

  ngOnInit() {
    this.formCreateGroup = new FormGroup({
      nameGroup: new FormControl('', [Validators.required]),
      login: new FormControl('', [Validators.required]),
      password: new FormControl('', [Validators.required]),
    })
  }

  protected manageDialog() {
    this.isOpen.emit(false);
  }

  submit(){
    this.createGroup();
  }

  createGroup(): void {
    const form = this.formCreateGroup?.value;
    console.log(form)
    this.groupService.createGroup(
      form.nameGroup,
      form.login,
      form.password).subscribe(
      data => {
        console.log(data);
        console.log('create_group');
          this.manageDialog();
    },
      error => {
        console.log(error);
        this.error = 'Группа с данным login уже существует';
        console.log(this.error);
      });
  }
}
<tui-root>
  <div class="wrapper"></div>
  <div class="dialog__container">
    <div class="dialog__container_outer">
      <div class="dialog__container_inner">
        <h2 class="dialog__title">Добавить группу</h2>
        <div>
          <form class="dialog__form" [formGroup]="formCreateGroup" (ngSubmit)="submit()">
            <label class="dialog__label">Название</label>
            <input type="text" name="date_interview" formControlName="nameGroup"/>

            <label class="dialog__label">login</label>
            <input type="text" name="date_interview" formControlName="login"/>

            <label class="dialog__label">Пароль</label>
            <input type="text" name="date_interview" formControlName="password"/>
            <div>{{error}}</div>
          </form>
        </div>

        <div class="dialog__buttons">
          <button tuiButton type="submit" class="dialog__add" size="m" (click)="submit()">
            Добавить
          </button>
          <button tuiButton type="button" appearance="outline"
                  class="tui-space_right-3 tui-space_bottom-3 dialog__cancel" size="m"
                  (click)="manageDialog()">
            Отмена
          </button>
        </div>
      </div>
    </div>
  </div>
</tui-root>
