import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { MainComponent } from "./components/main/main.component";
import {CommonModule} from "@angular/common";
import {StudentComponent} from "./components/student/student.component";
import {RegistrationComponent} from "./components/registration/registration.component";
import {LoginComponent} from "./components/login/login.component";
import {ProfileComponent} from "./components/profile/profile.component";
import {GroupStudentComponent} from "./components/teacher/components/group-student/group-student.component";
import {ConstructorComponent} from "./components/constructor/constructor.component";
import {GroupDetailedStudentComponent} from "./components/teacher/components/group-detailed-student/group-detailed-student.component";
import {GroupListStudentComponent} from "./components/teacher/components/group-list-student/group-list-student.component";
import {TeacherComponent} from "./components/teacher/teacher.component";
import {TasksStudentComponent} from "./components/student/components/tasks-student/tasks-student.component";
import {StudentMainComponent} from "./components/student/components/student-main/student-main.component";

const routesGroupTeacher: any =
  { path: 'group', component: GroupStudentComponent, children: [
    { path: 'list', component: GroupListStudentComponent},
    { path: 'details/:id', component: GroupDetailedStudentComponent},
    ]
  };

const routesTaskTeacher: any =
  { path: 'task', component: TasksStudentComponent, children: [
    // { path: 'list', component: TaskListTeacherComponent},
    // { path: 'details/:id', component: TaskDetailsTeacherComponent},
    ]
  };

const routesConstructorTeacher: any =
  { path: 'constructor', component: TasksStudentComponent, children: [
      // { path: 'task', component: GroupListStudentComponent},
      // { path: 'questions', component: GroupDetailedStudentComponent},
    ]
  };

const routes: Routes = [
  { path: '', redirectTo: 'main', pathMatch: 'full' },
  { path: 'main', component: MainComponent },
  { path: 'student', component: StudentComponent, children: [
      { path: 'main', component: StudentMainComponent}
    ]},
  { path: 'teacher', component: TeacherComponent, children: [
    routesConstructorTeacher, routesTaskTeacher, routesGroupTeacher,
    ]
  },
  { path: 'profile', component: ProfileComponent },
  { path: 'constructor', component: ConstructorComponent },
  { path: 'login', component: LoginComponent },
  { path: 'registration', component: RegistrationComponent },
  { path: 'tasks', component: TasksStudentComponent},
  // { path: '**', redirectTo: 'main'},
];

@NgModule({
  imports: [
    RouterModule.forRoot(routes),
    CommonModule
  ],
  exports: [RouterModule]
})
export class AppRoutingModule { }
