import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";
import {Task} from "../components/task-list/task-list.component";

@Injectable({
  providedIn: 'root'
})
export class GroupService {
constructor(private http: HttpClient) { }
  private apiUrl = 'http://127.0.0.1:8000/api/tasks/';

  getGroups(): Observable<object[]> {
      return this.http.get<object[]>(this.apiUrl);
  }

  createTask(newTask: any) {
      this.http.post(this.apiUrl, newTask);
  }
}
